```groovy
import java.util.UUID

// This function generates a specified number of UUIDs in a list
def generateUUIDs(int count) {
    // Define a string that contains all 16 hexadecimal digits
    String hexDigits = "0123456789abcdef"
    // Create a Random object to generate random numbers
    Random random = new Random()
    // Create an empty list to hold the generated UUIDs
    List<UUID> uuids = new ArrayList<>()
    // Repeat the following block of code `count` times
    for (int j = 0; j < count; j++) {
        // Generate the first three groups of hexadecimal digits separated by hyphens
        StringBuilder sb = new StringBuilder()
        for (int I = 0; I < 8; i++) {
            sb.append(hexDigits.charAt(random.nextInt(16)))
        }
        sb.append("-")
        for (int I = 0; I < 4; i++) {
            sb.append(hexDigits.charAt(random.nextInt(16)))
        }
        sb.append("-1") // Version 1 of UUID
        for (int I = 0; I < 3; i++) {
            sb.append(hexDigits.charAt(random.nextInt(16)))
        }
        sb.append("-")

        // Generate the fourth group of hexadecimal digits
        // Set the two most significant bits to 1 and 0, respectively
        sb.append(hexDigits.charAt((random.nextInt(4) + 8) << 4 | random.nextInt(16)))
        for (int I = 0; I < 3; i++) {
            sb.append(hexDigits.charAt(random.nextInt(16)))
        }
        sb.append("-")

        // Generate the fifth group of hexadecimal digits
        for (int I = 0; I < 12; i++) {
            sb.append(hexDigits.charAt(random.nextInt(16)))
        }

        // Convert the generated UUID string to a UUID object and add it to the list
        uuids.add(UUID.fromString(sb.toString()))
    }
    // Return the list of generated UUIDs
    return uuids
}

